import React from 'react';
import axios from 'axios';
import "./login.css";
const validator = require('email-validator');

class LoginForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {eValue : '',passValue: ''};
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      switch(event.target.name) {
        case 'emailField':
          this.setState({emailValue: event.target.value});
          break;
        case 'passField':
          this.setState({passwordValue: event.target.value});
          break;
      }
    }
    handleSubmit(event) {
        //submit button
        var account = {
            name: "",
            email: this.state.emailValue,
            password: this.state.passwordValue
        };
        var status = false;
        if(validator.validate(account["email"])){
            axios
            .post('http://localhost:3001/login', account)
            .then(function (response) {
                alert(response.data);
              })
            .catch(err => {
              console.error(err);
         });
        
      } 
      event.preventDefault();
    }
  //value={this.state.emailValue} name="emailField" onChange={this.handleChange}
  //value={this.state.passwordValue} name="passField" onChange={this.handleChange}

    render() {
      return (
        <div class = 'login-page-container'>
            <nav>
                <a class="nav-link" href="/"> Home</a> 
            </nav>
            <div name = 'container'>
                <form class = 'loginForm' onSubmit={this.handleSubmit}>
                    
                <div class="form-floating">
                    <input type="email" class="form-control" id="floatingInput" />
                    <label for="floatingInput">Email address</label>
                </div>
                <div class="form-floating">
                    <input type="password" class="form-control" id="floatingPassword" />
                    <label for="floatingPassword">Password</label>
                </div>
                    <input class = 'submit' type="submit"  name = "submit button" />
                    <br></br>
                    Dont Have an Account? <a class="nav-link" href="/signup"> Signup</a>
                </form>
                
            </div>
        </div>
        
      );
    }
  }
  export default LoginForm;
