import axios from 'axios';
import Cookies  from 'universal-cookie';

const auth = (jwt) => {
    //alert(jwt);
    if(jwt){
        //alert('hey');
        const cookies = new Cookies();

        axios
        .post("http://localhost:3001/auth",jwt)
        .then(function (response) {
                //alert(response.data);
                if(response.data)
                    cookies.set('isAuth',true);
                else cookies.set('isAuth',false);
            })
            .catch(err => {
                console.error(err);
            });
    }
    else {
        return false;
    }
}

export default auth;
