import React from "react";
import axios from 'axios';
import "./settings.css";


class Settings extends React.Component {
    
    constructor(props) {
      super(props);
      this.state = {name: '', position: '', email: '', image: {}};
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
    handleSubmit(event) {
        //need to edit a json on database
        //axios.post('http://localhost:3001/settings/addmember')
        
    }
    handleChange(event) {
        //alert(event.target.placeholder)
        switch(event.target.placeholder) {
            case "Name":
                alert("name")
                break;
            case "Position":
                alert("ehyt")
                break;
        }
    }
    render() {
        return (
            <div>
                <div className = 'cotainer-fluid'>
                    <nav>
                        <div className="form-check form-check-inline nowrap">
                            <a className="nav-link" href="/"> Home</a> |
                            <a className="nav-link" href="/calendar"> Calendar</a> |
                            <a className="nav-link" href="/profile"> Profile</a> |
                            <a className="nav-link" href="/signout"> Sign Out</a>
                        </div>  
                    </nav>
                </div> 
                <br></br>
                <div className = "container-fluid">
                    <form onSubmit={this.handleSubmit}>
                        <h4>Add Member</h4>
                        <div className = 'input-group mb-3'>
                            <input type="text" className ='form-control' placeholder = 'Name' value={this.state.name}  onChange={this.handleChange}/>
                        </div>
                        <div className = 'input-group mb-3'>
                            <input type="text" className ='form-control' placeholder = 'Position' value={this.state.position}  onChange={this.handleChange}/>
                        </div>
                        <div className = 'input-group mb-3'>
                            <input type="text" className ='form-control' placeholder = 'Email' value={this.state.email}  onChange={this.handleChange}/>
                        </div>
                        
                        <div className="input-group mb-3">
                            <input
                            type="file"
                            name="file"
                            id="input-files"
                            className="form-control-file border"
                            />
                        </div>
                        <input className = 'submit' type="submit" value = 'Sign Up'  name = "submit button" />
                    </form>
                </div>
            </div>
            
    );}
}
export default Settings;

