import React from 'react';
import axios from 'axios';
import "./login.css";
const validator = require('email-validator');

class LoginForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {eValue : '',passValue: ''};
  
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }
  
    handleChange(event) {
      switch(event.target.name) {
        case 'emailField':
          this.setState({emailValue: event.target.value});
          break;
        case 'passField':
          this.setState({passwordValue: event.target.value});
          break;
      }
    }
    handleSubmit(event) {
        //submit button
        var account = {
            name: "",
            email: this.state.emailValue,
            password: this.state.passwordValue
        };
        var status = false;
        if(validator.validate(account["email"])){
            axios
            .post('http://localhost:3001/login', account)
            .then(function (response) {
                alert(response.data);
              })
            .catch(err => {
              console.error(err);
         });
        
      } 
      event.preventDefault();
    }
  
    render() {
      return (
        <div class = 'login-page-container'>
            <nav>
                <a class="nav-link" href="/"> Home</a> 
            </nav>
            <div name = 'form-holder'>
                <form class = 'loginForm' onSubmit={this.handleSubmit}>
                    <div class = 'block'>
                        <label>Email: </label>
                        <input type="text" value={this.state.emailValue} name="emailField" onChange={this.handleChange} />
                    </div>
                
                    <div class = 'block'>
                        <label>Password: </label>
                        <input type="password" value={this.state.passwordValue} name="passField" onChange={this.handleChange} />
                    
                    </div>
                    <input type="submit" value="Submit" name = "submit button" />
                </form>
                Dont Have an Account? <a class="nav-link" href="/signup"> Signup</a>
            </div>
        </div>
        
      );
    }
  }
  export default LoginForm;
