import React from "react";
import axios from 'axios';
import Cookies  from 'universal-cookie';
//import Compress from "react-image-file-resizer";
import Compress from "browser-image-compression";
import auth from '../Login/auth';
import "./settings.css";



class Settings extends React.Component {
    
    constructor(props) {
      super(props);
      this.state = {addName: '', addPosition: '', addAbout: '', addEmail: ''};
      this.handleChange = this.handleChange.bind(this);
      this.handleAddMemberSubmit = this.handleAddMemberSubmit.bind(this);
      this.onImageChange = this.onImageChange.bind(this);
    }

    
    //used code from https://dev.to/tesh254/compress-images-in-react-browser-image-compression-libary-3cja
    onImageChange = (e) => {
        const file = e.target.files[0]
        // Compression config
        const options = {
            // As the key specify the maximum size
            // Leave blank for infinity
            maxSizeMB: 2,
            // Use webworker for faster compression with
            // the help of threads
            useWebWorker: true
        }

        // Initialize compression
        // First argument is the file object from the input
        // Second argument is the options object with the
        // config
        Compress(file, options)
            .then(compressedBlob => {
                // Compressed file is of Blob type
                // You can drop off here if you want to work with a Blob file
                // If you want to work with the File
                // Let's convert it here, by adding a couple of attributes
                compressedBlob.lastModifiedDate = new Date()
                // Convert the blob to file
                const convertedBlobFile = new File([compressedBlob], file.name, { type: file.type, lastModified: Date.now()})
                //convert to base64 then set this state image to the base64
                var reader = new FileReader();
                    reader.readAsDataURL(convertedBlobFile); 
                    reader.onloadend = function() {
                    var base64data = reader.result;                
                    localStorage.setItem("img",base64data)
                    }
            })
            .catch(e => {
                console.log(e);
                // Show the user a toast message or notification that something went wrong while compressing file
            })
    }
    handleAddMemberSubmit = async(e) =>{

        
        const cookies = new Cookies();
        const jwt = cookies.get('token');
        auth(jwt)
            const member = {
                name: this.state.addName,
                position: this.state.addPosition,
                about: this.state.addAbout,
                email: this.state.addEmail,
                image: localStorage.getItem("img")
            }
            const request = {
                code: jwt,
                member: member
            }
            axios.post('http://localhost:3001/settings/addmember',request)
                .then(function (response) {
                    alert(response.data)
            });
        
    }
    handleRemoveMemberSubmit = async(e) => {

    }
    handleChange(event) {
        //alert(event.target.placeholder)
        switch(event.target.name) {
            case "addName":
                this.setState({addName: event.target.value})
                break;
            case "addPosition":
                this.setState({addPosition: event.target.value})
                break;
            case "addAbout":
                this.setState({addAbout: event.target.value});
                break;
            case "addEmail":
                this.setState({addEmail: event.target.value})
                break;
        }
    }
    render() {
        return (
            <div>
                <div className = 'cotainer-fluid'>
                    <nav>
                        <div className="form-check form-check-inline nowrap">
                            <a className="nav-link" href="/"> Home</a> |
                            <a className="nav-link" href="/calendar"> Calendar</a> |
                            <a className="nav-link" href="/profile"> Profile</a> |
                            <a className="nav-link" href="/signout"> Sign Out</a>
                        </div>  
                    </nav>
                </div> 
                <br></br>
                <div className = "container-fluid"> 
                    <div className='row'>
                        <div className = 'col-md-6'>
                            <form className = "holder-left" onSubmit={this.handleAddMemberSubmit}>
                                <h4>Add Display Member</h4>
                                <div className = 'input-group mb-3'>
                                    <input type="text" className ='form-control'name = 'addName' placeholder = 'Name' value={this.state.name}  onChange={this.handleChange}/>
                                </div>
                                <div className = 'input-group mb-3'>
                                    <input type="text" className ='form-control' name = 'addPosition' placeholder = 'Position' value={this.state.position}  onChange={this.handleChange}/>
                                </div>
                                <div className = 'input-group mb-3'>
                                    <input type="text" className ='form-control' name = 'addEmail' placeholder = 'Email' value={this.state.email}  onChange={this.handleChange}/>
                                </div>
                                <div className = 'input-group mb-3'>
                                    <textarea class="form-control" name="addAbout" placeholder = 'About' rows="4" value={this.state.abput} onChange={this.handleChange}/>
                                </div>
                                <div className="input-group mb-3">
                                    <input
                                    type="file"
                                    name="file"
                                    id="input-files"
                                    className="form-control-file border"
                                    onChange={this.onImageChange}
                                    />
                                </div>
                                <input className = 'submit' type="submit" value = 'Add Member'  name = "submit button" />
                            </form>
                        </div> 
                        <div className = 'col-md-6'>
                            <form className = "holder-right" onSubmit={this.handleAddmemberSubmit}>
                                <h4>Remove Display Member</h4>
                                <div className = 'input-group mb-3'>
                                    <input type="text" className ='form-control'name = 'name' placeholder = 'Name' value={this.state.name}  onChange={this.handleChange}/>
                                </div>
                                <input className = 'submit' type="submit" value = 'Remove Member'  name = "submit button" />
                            </form>
                            <form className = "holder-right" onSubmit={this.handleAddmemberSubmit}>
                                <h4>Remove Account</h4>
                                <div className = 'input-group mb-3'>
                                    <input type="text" className ='form-control'name = 'name' placeholder = 'Email' value={this.state.name}  onChange={this.handleChange}/>
                                </div>
                                <input className = 'submit' type="submit" value = 'Remove Account'  name = "submit button" />
                            </form>
                            <form className = "holder-right" onSubmit={this.handleAddmemberSubmit}>
                                <h4>Change Account Role</h4>
                                <div className = 'input-group mb-3'>
                                    <input type="text" className ='form-control'name = 'email' placeholder = 'Email' value={this.state.name}  onChange={this.handleChange}/>
                                </div>
                                <div className = 'input-group mb-3'>
                                    <input type="text" className ='form-control'name = 'role' placeholder = 'Role' value={this.state.name}  onChange={this.handleChange}/>
                                </div>
                                <input className = 'submit' type="submit" value = 'Change Role'  name = "submit button" />
                            </form>
                        </div> 
                        <div className = 'col-md-12'>
                            
                            <div className = 'input-group mb-3' id = 'box'>
                                <input type="text" className ='form-control'name = 'role' placeholder = 'Box Title' value={this.state.name}  onChange={this.handleChange}/>
                            </div>
                            <div className = 'input-group mb-3' id = 'box'>
                                    <textarea class="form-control" name="about" placeholder = 'Box Information' rows="4" value={this.state.abput} onChange={this.handleChange}/>
                            </div>
                        </div>
                    </div>   
                </div>
            </div>
            
    );}
    
}

//used code from https://bezkoder.com/node-js-upload-store-images-mongodb/
export default Settings;
